import React, { useState } from 'react'

function Hooks() {
  const [count, setCount] = useState(0)
  function minCount() {
    setCount(prevCount => prevCount - 0.5)
    setCount(prevCount => prevCount - 0.5)
  }
  function addCount() {
    setCount(count + 1)
  }
  return (
    <>
      <button onClick={minCount}>-</button>
      <span>{count}</span>
      <button onClick={addCount}>+</button>
    </>
  )
}
export default Hooks
