import Nav from '../components/Nav'
import React, { Component } from 'react'
import PropTypes from 'prop-types'

class Currency extends Component {
  constructor(props) {
    super(props)
    this.state = {
      available: [],
      base: 'HKD',
      target: 'USD',
      rate: 0,
      amount: 0,
      isLoading: false,
      isError: false,
    }
  }

  async componentDidMount() {
    this.setState({ isLoading: true })
    const response = await fetch(
      'https://openexchangerates.org/api/currencies.json',
    )
    if (response.ok) {
      const available = await response.json()
      this.setState({ available, isLoading: false })
    } else {
      this.setState({ isError: true, isLoading: false })
    }
  }
  renderAvailable = () => {
    return Object.keys(this.state.available).map((key, id) => {
      return (
        <option value={key} key={id}>
          {key} ({this.state.available[key]})
        </option>
      )
    })
  }
  setBase = (base) => {
    this.setState({ base })
  }
  setTarget = (target) => {
    this.setState({ target })
  }
  setAmount = (amount) => {
    if (amount !== '' && amount >= 0) {
      this.setState({ amount: parseInt(amount) })
    } else {
      this.setState({ amount: '' })
    }
  }
  onSubmit = (e) => {
    e.preventDefault()
    this.setState({ isLoading: true })
    this.updateRate()
  }
  updateRate = () => {
    const url =
      'https://api.ratesapi.io/api/latest?base=' +
      this.state.base +
      '&symbols=' +
      this.state.target
    fetch(url)
      .then((response) => response.json())
      .then((result) => {
        var target = this.state.target
        this.setState({ rate: result.rates[target] })
      })
  }

  render() {
    return (
      <div>
        <Nav />
        <div className="container">
          <h3>Currency Converter</h3>
          <form action="" onSubmit={this.onSubmit}>
            <div className="row">
              <div className="col-6">
                <div className="form-group">
                  <label htmlFor="">Base Currency</label>
                  <select
                    name="type"
                    id=""
                    className="form-control"
                    value={this.state.base}
                    onChange={(e) => this.setBase(e.target.value)}
                  >
                    {this.renderAvailable()}
                  </select>
                </div>
              </div>
              <div className="col-6">
                <div className="form-group">
                  <label htmlFor="">Target Currency</label>
                  <select
                    name="type"
                    id=""
                    className="form-control"
                    value={this.state.target}
                    onChange={(e) => this.setTarget(e.target.value)}
                  >
                    {this.renderAvailable()}
                  </select>
                </div>
              </div>
              <div className="col">Current Rate: {this.state.rate}</div>
            </div>
            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="">Amount (From)</label>
                  <input
                    type="number"
                    className="form-control"
                    value={this.state.amount}
                    onChange={(e) => this.setAmount(e.target.value)}
                  />
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="">Amount (To)</label>
                  <input type="number" className="form-control" disabled />
                </div>
              </div>
            </div>
            <button className="btn btn-success" type="submit">
              Convert
            </button>
          </form>
        </div>
      </div>
    )
  }
}
Currency.propTypes = {
  amount: PropTypes.number,
}

export default Currency
