import Nav from '../components/Nav'
import React, { Component } from 'react'

class Currency extends Component {
  constructor(props) {
    super(props)
    this.state = {
      available: [],
      base: '',
      curr_rate: 0,
      isLoading: false,
      isError: false,
    }
  }

  async componentDidMount() {
    this.setState({ isLoading: true })
    const response = await fetch(
      'https://openexchangerates.org/api/currencies.json',
    )
    if (response.ok) {
      const available = await response.json()
      this.setState({ available, isLoading: false })
    } else {
      this.setState({ isError: true, isLoading: false })
    }
  }
  renderAvailable = () => {
    return Object.keys(this.state.available).map((key, id) => {
      return (
        <option value={key} key={id}>
          {key} ({this.state.available[key]})
        </option>
      )
    })
  }
  setBase = ({ base }) => {
    this.state.base = base
  }
  setTarget = ({ target }) => {
    this.state.target = target
  }


  render() {
    return (
      <div>
        <Nav />
        <div className="container">
          <h3>Currency Converter</h3>
          <form action="">
            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="">Base Currency</label>
                  <select
                    name="type"
                    id=""
                    className="form-control"
                    onChange={(e) => this.setBase(e.target.value)}
                  >
                    {this.renderAvailable()}
                  </select>
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="">Target Currency</label>
                  <select
                    name="type"
                    id=""
                    className="form-control"
                    onChange={(e) => this.setBase(e.target.value)}
                  >
                    {this.renderAvailable()}
                  </select>
                </div>
                </div>
            </div>
            <div className="form-group">
              <label htmlFor="">Amount (From)</label>
              <input type="number" className="form-control" />
            </div>
            <div className="form-group">
              <label htmlFor="">Amount (To)</label>
              <input type="number" className="form-control" disabled />
            </div>
            <button className="btn btn-success" type="submit">
              Convert
            </button>
          </form>
        </div>
      </div>
    )
  }
}
export default Currency
