const Tasks = () => {
    const [tasks,setTasks]= useState([
        {
          id: 1,
          text: 'A',
          day: '2020-01-25',
          reminder: true,
        },
        {
          id: 2,
          text: 'B',
          day: '2020-05-25',
          reminder: false,
        },
        {
          id: 3,
          text: 'C',
          day: '2019-05-25',
          reminder: false,
        },
      ])
  return (
    <div>
      {tasks.map((task) => (
        <h3 key={task.id} className='card'>{task.text}</h3>
      ))}
    </div>
  )
}

export default Tasks
