import Nav from '../components/Nav'
import React, { Component } from 'react'

class Currency extends Component {
  constructor(props) {
    super(props)
    this.state = {
      available: [],
      base: '',
      target: '',
      rate: 0,
      amount: 0,
      isLoading: false,
      isError: false,
    }
  }

  async componentDidMount() {
    this.setState({ isLoading: true })
    const response = await fetch(
      'https://openexchangerates.org/api/currencies.json',
    )
    if (response.ok) {
      const available = await response.json()
      this.setState({ available, isLoading: false })
    } else {
      this.setState({ isError: true, isLoading: false })
    }
  }
  renderAvailable = () => {
    return Object.keys(this.state.available).map((key, id) => {
      return (
        <option value={key} key={id}>
          {key} ({this.state.available[key]})
        </option>
      )
    })
  }
  setBase = (base) => {
    this.setState({ base })
    const response = fetch(
      'https://openexchangerates.org/api/convert/' +
        this.state.amount +
        '/' +
        this.state.base +
        '/' +
        this.state.target +
        '?app_id=771d7e8d54654803aedb309c86a336d0',
    )
  }
  setTarget = (target) => {
    this.setState({ target })
  }
  setAmount = (amount) => {
    this.setState({ amount })
  }

  render() {
    return (
      <div>
        <Nav />
        <div className="container">
          <h3>Currency Converter</h3>
          <form action="">
            <div className="row">
              <div className="col-6">
                <div className="form-group">
                  <label htmlFor="">Base Currency</label>
                  <select
                    name="type"
                    id=""
                    className="form-control"
                    value={this.state.base}
                    onChange={(e) => this.setBase(e.target.value)}
                  >
                    {this.renderAvailable()}
                  </select>
                </div>
              </div>
              <div className="col-6">
                <div className="form-group">
                  <label htmlFor="">Target Currency</label>
                  <select
                    name="type"
                    id=""
                    className="form-control"
                    value={this.state.target}
                    onChange={(e) => this.setTarget(e.target.value)}
                  >
                    {this.renderAvailable()}
                  </select>
                </div>
              </div>
              <div className="col">Current Rate: {this.state.rate}</div>
            </div>
            <div className="row">
              <div className="col">
                <div className="form-group">
                  <label htmlFor="">Amount (From)</label>
                  <input
                    type="number"
                    className="form-control"
                    onChange={this.setAmount()}
                  />
                </div>
              </div>
              <div className="col">
                <div className="form-group">
                  <label htmlFor="">Amount (To)</label>
                  <input type="number" className="form-control" disabled />
                </div>
              </div>
            </div>
            <button className="btn btn-success" type="submit">
              Convert
            </button>
          </form>
        </div>
      </div>
    )
  }
}
export default Currency
