import Nav from '../components/Nav'
import React, { Component } from 'react'

class Currency extends Component {
  constructor(props) {
    super(props)
    this.state = {
      available: [],
      curr_base: '',
      curr_rate: 0,
      isLoading: false,
      isError: false,
    }
  }

  async componentDidMount() {
    this.setState({ isLoading: true })
    const response = await fetch(
      'https://openexchangerates.org/api/currencies.json',
    )
    if (response.ok) {
      const available = await response.json()
      this.setState({ available, isLoading: false })
    } else {
      this.setState({ isError: true, isLoading: false })
    }
  }
  renderAvailable = () => {
    return Object.keys(this.state.available).forEach((key)=> {
      return (
        <option value={key} key={key}>
          {this.state.available[key]}
        </option>
      )
    })
  }
  setCurrBase = ({ base }) => {
    this.state.curr_base = base
  }

  render() {
    return (
      <div>
        <Nav />
        <div className="container">
          <h3>Currency Converter</h3>
          <form action="">
            <div className="form-group">
              <label htmlFor="">Base Currency</label>
              <select
                name="type"
                id=""
                className="form-control"
                onChange={(e) => this.setCurrBase(e.target.value)}
              >
                {this.renderAvailable()}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="">Amount (From)</label>
              <input type="number" className="form-control" />
            </div>
            <div className="form-group">
              <label htmlFor="">Amount (To)</label>
              <input type="number" className="form-control" disabled />
            </div>
            <button className="btn btn-success" type="submit">
              Convert
            </button>
          </form>
        </div>
      </div>
    )
  }
}
export default Currency
