import Nav from '../components/Nav'
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Line } from 'react-chartjs-2'
import { pastDate, todayDate } from '../libraries/datetime'

class Currency extends Component {
  constructor(props) {
    super(props)
    this.state = {
      available: [],
      base: 'HKD',
      target: 'USD',
      rate: 0,
      timeSeries: [],
      show_days: 30,
      start_date: pastDate(30),
      end_date: todayDate(),
      amount: 888,
      result: 0,
      isLoading: false,
      isError: false,
      chartData: {},
    }
  }

  async componentDidMount() {
    this.setState({ isLoading: true })
    const response = await fetch(
      'https://openexchangerates.org/api/currencies.json',
    )
    if (response.ok) {
      const available = await response.json()
      this.setState({ available, isLoading: false })
    } else {
      this.setState({ isError: true, isLoading: false })
    }
    this.updateRate()
  }
  renderAvailable = () => {
    return Object.keys(this.state.available).map((key, id) => {
      return (
        <option value={key} key={id}>
          {key} ({this.state.available[key]})
        </option>
      )
    })
  }
  setBase = (base) => {
    this.setState({ base })
    this.updateRate()
  }
  setTarget = (target) => {
    this.setState({ target })
    this.updateRate()
  }
  setAmount = (amount) => {
    if (amount !== '' && amount >= 0) {
      this.setState({ amount: parseInt(amount) })
    } else {
      this.setState({ amount: '' })
    }
  }
  onSubmit = (e) => {
    if (e) {
      e.preventDefault()
    }
    this.setState({ result: this.state.rate * this.state.amount })
  }
  async updateRate() {
    const url =
      'https://api.exchangerate.host/convert?from=' +
      this.state.base +
      '&to=' +
      this.state.target
    const response = await fetch(url)
    if (response.ok) {
      const result = await response.json()
      this.setState({ rate: result.result })
      this.getRateTS()
      this.onSubmit()
    }
  }
  getRateTS = () => {
    const url =
      'https://api.exchangerate.host/timeseries?start_date=' +
      this.state.start_date +
      '&end_date=' +
      this.state.end_date +
      '&base=' +
      this.state.base +
      '&symbols=' +
      this.state.target
    fetch(url)
      .then((response) => response.json())
      .then((result) => {
        this.setState({ timeSeries: result.rates })
      })
  }
  showRateTS = () => {
    const datetime = Object.keys(this.state.timeSeries)
    const daterate = Object.values(this.state.timeSeries)
    var arr = []
    datetime.map((key, id) => {
      return arr.push(daterate[id][this.state.target])
    })
    const data = {
      labels: datetime,
      datasets: [
        {
          label:
            'Exchange Rate from ' +
            this.state.base +
            ' to ' +
            this.state.target +
            ' in Past ' +
            this.state.show_days +
            ' Days',
          data: arr,
          fill: false,
          backgroundColor: 'rgb(255, 99, 132)',
          borderColor: 'rgba(255, 99, 132, 0.2)',
        },
      ],
    }
    const options = {
      scales: {
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
            },
          },
        ],
      },
    }
    return <Line data={data} options={options} />
  }

  render() {
    return (
      <div>
        <Nav />
        <div className="container p-4">
          <div className="card">
            <h3>Currency Converter</h3>
            <form action="" onSubmit={this.onSubmit}>
              <div className="row">
                <div className="col-6">
                  <div className="form-group">
                    <label htmlFor="">Base Currency</label>
                    <select
                      name="type"
                      id=""
                      className="form-control"
                      value={this.state.base}
                      onChange={(e) => this.setBase(e.target.value)}
                    >
                      {this.renderAvailable()}
                    </select>
                  </div>
                </div>
                <div className="col-6">
                  <div className="form-group">
                    <label htmlFor="">Target Currency</label>
                    <select
                      name="type"
                      id=""
                      className="form-control"
                      value={this.state.target}
                      onChange={(e) => this.setTarget(e.target.value)}
                    >
                      {this.renderAvailable()}
                    </select>
                  </div>
                </div>
                <div className="col text-center py-2">
                  Current Rate: {this.state.rate}
                </div>
              </div>
              <div className="row">
                <div className="col">
                  <div className="form-group">
                    <label htmlFor="">Amount (From)</label>
                    <input
                      type="number"
                      className="form-control"
                      value={this.state.amount}
                      onChange={(e) => this.setAmount(e.target.value)}
                    />
                  </div>
                </div>
                <div className="col">
                  <div className="form-group">
                    <label htmlFor="">Amount (To)</label>
                    <input
                      type="number"
                      className="form-control"
                      value={this.state.result}
                      disabled
                    />
                  </div>
                </div>
              </div>
              <button className="btn btn-success" type="submit">
                Convert
              </button>
            </form>
            <hr />
            <h3>Past Exchange Rate</h3>
            {this.showRateTS()}
          </div>
        </div>
      </div>
    )
  }
}
Currency.propTypes = {
  amount: PropTypes.number,
}

export default Currency
